import logo from './logo.svg';
import React from 'react'
import './App.css';

class App extends React.Component {

  constructor(props){
    super(props)

    this.state = {
      user: ['A','B','C'],
      name : 'Guillaume'
    }
  }

  componentDidMount(){
    console.log('Compoent did mount')
    this.setState({
      name : 'Le tricheur'
    }, () => console.log('call back :' , this.state.name))

  }

  render() {
    return (
      <div className="App">
        {
          this.state.user.map((user,i) => <p key={i}>{user}</p>)
        }
      </div> 
    );
  }

}

export default App;
